import { useState, useEffect } from 'react';
import NewsCard from '../NewsCard/NewsCard';
import cards from '../../utils/constants';

function NewsCardList() {
    const [quantityCards , setQuantityCards] = useState(Math.min(3,cards.length));
    
    function showMore() {
        if (cards.length > quantityCards) {
            setQuantityCards(Math.min(quantityCards + 3, cards.length));
        }
    }


    return (
        <section className="news-card-list">
            <h2 className="news-card-list__title">Search results</h2>
            <ul className="news-card-list__cards-grid">
                {
                    cards.map((card, ind) => {
                        for (ind = 0; i<=quantityCards ; i++ ) {  
                            return(<NewsCard card={card} key={ind}/>)
                        }
                    })
                }
            </ul>
            <button className="news-card__show-more-button" onClick={showMore}>
                Show more
            </button>
        </section>
    );
}

export default NewsCardList;
