import Navigation from '../Navigation/Navigation';
import { withRouter} from "react-router-dom";

function Header(e) {
  
  function changeMenuIcon(e) {
      console.log(e.target);
      const icon = e.target;
      icon.classList.toggle("header__menu-icon");
      icon.classList.toggle("header__menu-icon_close");
  
  }
  
  return (
        <header className={ window.location.href.includes("/saved-news") ? "header header_black": "header"}>
          <div>NewsExplorer</div>
          <Navigation isLogged={true} name="Eliseeeeeeeeeeeeee"/>
          <div 
            className={window.location.href.includes("/saved-news") 
              ? "header__menu-icon header__menu-icon_black" 
              : "header__menu-icon"}
            onClick={changeMenuIcon}
          ></div>
        </header>
    );
}

export default withRouter(Header);
