import './App.css';
import { useState, useEffect } from 'react';
import { Route, Switch, Redirect, useHistory } from 'react-router-dom';
import Header from '../Header/Header'
import Footer from '../Footer/Footer';
import Main from '../Main/Main';
import SavedNews from '../SavedNews/SavedNews';
import SavedNewsHeader from '../SavedNewsHeader/SavedNewsHeader';
import About from '../About/About';
import Preloader from '../Preloader/Preloader';
import NewsCardList from '../NewsCardList/NewsCardList';
import ErrorSearch from '../ErrorSearch/ErrorSearch';
import cards from '../../utils/constants';

function App() {
  const [strKeywords, setStrKeywords] = useEffect('');

  function handleSearchSubmit(tag) {
    alert(tag);
  }

  useEffect(() => {
    const keywords = cards.reduce((previousValue, item, index, array) => {
      const result = previousValue.find(el => {
        if (el) {
          return el.keyword === item.keyword;
        } else {
          return false;
        }
      });
      if (!result) {
        previousValue.push({keyword: item.keyword, count: 1})
      } else {
        previousValue[previousValue.indexOf(result)].count++;
      }      
      return previousValue.sort((a, b) => a.count < b.count ? 1 : -1);
    },[]);
    // if (keywords.length > 3) {
    //   setStrKeywords(`${keywords[0].keyword}, ${keywords[1].keyword}, and 2 other`)
    // } else {
    //   setStrKeywords("many");
    //   // setStrKeywords(
    //   //   keywords.reduce((arrKewords,item) => arrKewords.push(item.keyword)
    //   //   ).join(", "));
    // }
  })

  return (
    <div className="page">
      <Header />
      <Switch>
        <Route exact path="/">
          <Main search={handleSearchSubmit}/>
          <Preloader />
          <ErrorSearch />
          <NewsCardList />
          <About />         
        </Route> 
        <Route exact path="/saved-news">
          <SavedNewsHeader 
            name="Elise" 
            quantitySavedCards={cards.length}
            strKeywords={strKeywords}
          />
          <SavedNews />
        </Route>         
      </Switch>
      <Footer />
    </div>
  );
}

export default App;
