import './LoginButton.css';

function LoginButton(props) {
if(props.isLogged){
        return (
            <div>
                <span>{props.name}</span>
                <i className="login-button__exit-icon"></i>
            </div>
        );
    } else {
        return (
            <span>Sign in</span>
        );
    }

}

export default LoginButton;
